function attributes_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('attributes');
	if(!$arrPerm[1]){
		return false;
	}

	$data['attribute'] = makeSafe($_REQUEST['attribute']);
		if($data['attribute'] == empty_lookup_value){ $data['attribute'] = ''; }
	$data['value'] = br2nl(makeSafe($_REQUEST['value']));
	$data['contact'] = makeSafe($_REQUEST['contact']);
		if($data['contact'] == empty_lookup_value){ $data['contact'] = ''; }
	$data['companies'] = makeSafe($_REQUEST['companies']);
		if($data['companies'] == empty_lookup_value){ $data['companies'] = ''; }
	$data['products'] = makeSafe($_REQUEST['products']);
		if($data['products'] == empty_lookup_value){ $data['products'] = ''; }

	// hook: attributes_before_insert
	if(function_exists('attributes_before_insert')){
		$args=array();
		if(!attributes_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `attributes` set       `attribute`=' . (($data['attribute'] !== '' && $data['attribute'] !== NULL) ? "'{$data['attribute']}'" : 'NULL') . ', `value`=' . (($data['value'] !== '' && $data['value'] !== NULL) ? "'{$data['value']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"attributes_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// automatic contact
	if($_REQUEST['filterer_contact']){
		sql("update `attributes` set `contact`='" . makeSafe($_REQUEST['filterer_contact']) . "' where `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	// automatic companies
	if($_REQUEST['filterer_companies']){
		sql("update `attributes` set `companies`='" . makeSafe($_REQUEST['filterer_companies']) . "' where `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	// automatic products
	if($_REQUEST['filterer_products']){
		sql("update `attributes` set `products`='" . makeSafe($_REQUEST['filterer_products']) . "' where `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	// hook: attributes_after_insert
	if(function_exists('attributes_after_insert')){
		$res = sql("select * from `attributes` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!attributes_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('attributes', $recID, getLoggedMemberID());

	return $recID;
}

function attributes_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('attributes');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='attributes' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='attributes' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: attributes_before_delete
	if(function_exists('attributes_before_delete')){
		$args=array();
		if(!attributes_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `attributes` where `id`='$selected_id'", $eo);

	// hook: attributes_after_delete
	if(function_exists('attributes_after_delete')){
		$args=array();
		attributes_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='attributes' and pkValue='$selected_id'", $eo);
}

function attributes_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('attributes');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='attributes' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='attributes' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['attribute'] = makeSafe($_REQUEST['attribute']);
		if($data['attribute'] == empty_lookup_value){ $data['attribute'] = ''; }
	$data['value'] = br2nl(makeSafe($_REQUEST['value']));
	$data['contact'] = makeSafe($_REQUEST['contact']);
		if($data['contact'] == empty_lookup_value){ $data['contact'] = ''; }
	$data['companies'] = makeSafe($_REQUEST['companies']);
		if($data['companies'] == empty_lookup_value){ $data['companies'] = ''; }
	$data['products'] = makeSafe($_REQUEST['products']);
		if($data['products'] == empty_lookup_value){ $data['products'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: attributes_before_update
	if(function_exists('attributes_before_update')){
		$args=array();
		if(!attributes_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `attributes` set       `attribute`=' . (($data['attribute'] !== '' && $data['attribute'] !== NULL) ? "'{$data['attribute']}'" : 'NULL') . ', `value`=' . (($data['value'] !== '' && $data['value'] !== NULL) ? "'{$data['value']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="attributes_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: attributes_after_update
	if(function_exists('attributes_after_update')){
		$res = sql("SELECT * FROM `attributes` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!attributes_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='attributes' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function attributes_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = ''){
