function electronicInvoice_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('electronicInvoice');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='electronicInvoice' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='electronicInvoice' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: electronicInvoice_before_delete
	if(function_exists('electronicInvoice_before_delete')){
		$args=array();
		if(!electronicInvoice_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `electronicInvoice` where `id`='$selected_id'", $eo);

	// hook: electronicInvoice_after_delete
	if(function_exists('electronicInvoice_after_delete')){
		$args=array();
		electronicInvoice_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='electronicInvoice' and pkValue='$selected_id'", $eo);
}

