			return $Translation['Couldn\'t delete this record'];
	}

	// child table: ordersDetails
	$res = sql("select `id` from `orders` where `id`='$selected_id'", $eo);
	$id = db_fetch_row($res);
	$rires = sql("select count(1) from `ordersDetails` where `order`='".addslashes($id[0])."'", $eo);
