	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"town_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: town_after_insert
	if(function_exists('town_after_insert')){
		$res = sql("select * from `town` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!town_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

