		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return town_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if($_REQUEST['Embedded']){
		$backAction = 'AppGini.closeParentModal(); return false;';
	}else{
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id){
		if(!$_REQUEST['Embedded']) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate){
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return town_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		}else{
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	}else{
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#country').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#country_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#idIstat').replaceWith('<div class=\"form-control-static\" id=\"idIstat\">' + (jQuery('#idIstat').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#town').replaceWith('<div class=\"form-control-static\" id=\"town\">' + (jQuery('#town').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#district').replaceWith('<div class=\"form-control-static\" id=\"district\">' + (jQuery('#district').val() || '') + '</div>'); jQuery('#district-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('#region').replaceWith('<div class=\"form-control-static\" id=\"region\">' + (jQuery('#region').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#prefix').replaceWith('<div class=\"form-control-static\" id=\"prefix\">' + (jQuery('#prefix').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#shipCode').replaceWith('<div class=\"form-control-static\" id=\"shipCode\">' + (jQuery('#shipCode').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#fiscCode').replaceWith('<div class=\"form-control-static\" id=\"fiscCode\">' + (jQuery('#fiscCode').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#inhabitants').replaceWith('<div class=\"form-control-static\" id=\"inhabitants\">' + (jQuery('#inhabitants').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#link').replaceWith('<div class=\"form-control-static\" id=\"link\">' + (jQuery('#link').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#link, #link-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(country)%%>', $combo_country->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(country)%%>', $combo_country->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(country)%%>', urlencode($combo_country->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(district)%%>', $combo_district->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(district)%%>', $combo_district->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'country' => array('countries', 'Country'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(country)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(idIstat)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(town)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(district)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(region)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(prefix)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(shipCode)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(fiscCode)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(inhabitants)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(link)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(country)%%>', safe_html($urow['country']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(country)%%>', html_attr($row['country']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(country)%%>', urlencode($urow['country']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(idIstat)%%>', safe_html($urow['idIstat']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(idIstat)%%>', html_attr($row['idIstat']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(idIstat)%%>', urlencode($urow['idIstat']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(town)%%>', safe_html($urow['town']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(town)%%>', html_attr($row['town']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(town)%%>', urlencode($urow['town']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(district)%%>', safe_html($urow['district']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(district)%%>', html_attr($row['district']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(district)%%>', urlencode($urow['district']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(region)%%>', safe_html($urow['region']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(region)%%>', html_attr($row['region']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(region)%%>', urlencode($urow['region']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(prefix)%%>', safe_html($urow['prefix']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(prefix)%%>', html_attr($row['prefix']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(prefix)%%>', urlencode($urow['prefix']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(shipCode)%%>', safe_html($urow['shipCode']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(shipCode)%%>', html_attr($row['shipCode']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(shipCode)%%>', urlencode($urow['shipCode']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(fiscCode)%%>', safe_html($urow['fiscCode']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(fiscCode)%%>', html_attr($row['fiscCode']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(fiscCode)%%>', urlencode($urow['fiscCode']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(inhabitants)%%>', safe_html($urow['inhabitants']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(inhabitants)%%>', html_attr($row['inhabitants']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(inhabitants)%%>', urlencode($urow['inhabitants']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(link)%%>', safe_html($urow['link']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(link)%%>', html_attr($row['link']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(link)%%>', urlencode($urow['link']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(country)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(country)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(idIstat)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(idIstat)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(town)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(town)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(district)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(district)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(region)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(region)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(prefix)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(prefix)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(shipCode)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(shipCode)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(fiscCode)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(fiscCode)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(inhabitants)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(inhabitants)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(link)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(link)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
			$templateCode.="\n\tif(document.getElementById('linkEdit')){ document.getElementById('linkEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('linkEditLink')){ document.getElementById('linkEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('town');
	if($selected_id){
		$jdata = get_joined_record('town', $selected_id);
		if($jdata === false) $jdata = get_defaults('town');
		$rdata = $row;
	}
	$templateCode .= loadView('town-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: town_dv
	if(function_exists('town_dv')){
		$args=array();
		town_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
